import React, { useState } from "react";

import "./App.css";

import Filter from "./Filter/Filter";
import MovieList from "./MovieCard/MovieList";
import AddMovie from "./AddMovie";
import MovieDetails from "./MovieDetails";

import { Route, Switch } from "react-router-dom";

function App() {
  const [ListOfMovies, setListOfMovies] = useState([
    {
      title: "The Old Guard",
      description:
        "A group of mercenaries, all centuries-old immortals with the ablity to heal themselves, discover someone is onto their secret, and they must fight to protect their freedom.",
      posterUrl:
        "https://m.media-amazon.com/images/M/MV5BNDJiZDliZDAtMjc5Yy00MzVhLThkY2MtNDYwNTQ2ZTM5MDcxXkEyXkFqcGdeQXVyMDA4NzMyOA@@._V1_.jpg",
      rate: {
        size: 50,
        count: 5,
        value: 5,
        edit: false,
      },
      id: "1",
      watched: false,
    },
    {
      title: "Mulan",
      description:
        "To save her ailing father from serving in the Imperial Army, a fearless young woman disguises herself as a man to battle northern invaders in China.",
      posterUrl:
        "https://images-na.ssl-images-amazon.com/images/I/71c%2BSZbaVVL._AC_SL1293_.jpg",
      rate: {
        size: 50,
        count: 5,
        value: 3,
        edit: false,
      },
      id: "2",
      watched: false,
    },
    {
      title: "Greenland",
      description:
        "John Garrity, his estranged wife and their young son embark on a perilous journey to find sanctuary as a planet-killing comet hurtles toward Earth. Amid terrifying accounts of cities getting levelled, the Garrity's experience the best and worst in humanity. ",
      posterUrl:
        "data:image/jpeg;base64,",
      rate: {
        size: 50,
        count: 5,
        value: 5,
        edit: false,
      },
      id: "3",
      watched: false,
    },
    {
      title: "Extraction",
      description:
        "A black-market mercenary who has nothing to lose is hired to rescue the kidnapped son of an imprisoned international crime lord. But in the murky underworld of weapons dealers and drug traffickers, an already deadly mission approaches the impossible.",
      posterUrl:
        "data:image/jpeg;base64,",
      rate: {
        size: 50,
        count: 5,
        value: 3,
        edit: false,
      },
      id: "4",
      watched: false,
    },
  ]);

  const modification = (modif) =>
    modif
      ? setListOfMovies([
          ...ListOfMovies,
          {
            title: modif.title,
            description: modif.description,
            posterUrl: modif.posterURl,
            rate: {
              size: 50,
              count: 5,
              value: modif.rate.value,
              edit: false,
            },
            id: Math.round(),
            watched: false,
          },
        ])
      : null;

  const [search, setSearch] = useState("");
  const searchbytitle = (value) => {
    setSearch(value);
  };
  const [ratefiltring, setRatefiltring] = useState(0);
  const searchbyrate = (value) => {
    setRatefiltring(value);
  };
  const handleRemove = (id) =>
    setListOfMovies(ListOfMovies.filter((el) => el.id !== id));
  const handlemovie = (id) =>
    setListOfMovies(
      ListOfMovies.map((movie) =>
        movie.id === id
          ? {
              title: movie.title,
              description: movie.description,
              posterUrl: movie.posterUrl,
              rate: movie.rate,
              id: movie.id,
              watched: !movie.watched,
            }
          : movie
      )
    );

  return (
    <div className="App">
      <Switch>
        <Route exact path="/">
          <Filter searchbytitle={searchbytitle} searchbyrate={searchbyrate} />
          <MovieList
            ListOfMovies={ListOfMovies}
            handleRemove={handleRemove}
            search={search}
            ratefiltring={ratefiltring}
          />
          <AddMovie modification={modification} />
        </Route>
        <Route
          path="/:title"
          render={(props) => <MovieDetails data={ListOfMovies} {...props} />}
        />
      </Switch>
    </div>
  );
}

export default App;
